def algo_d_N(d, e, N):
    k = d * e - 1
    while True:
        g = random.randint(1, N)
        t = 0
        p = q = None
        while True:
            t += 1
            tt = 2**t
            ttt = k//tt
            x = pow(g, ttt, N)
            if x <= 1:
                continue
            y = math.gcd(x - 1, N)
            if y > 1:
                p = y
                q = N//y
                break
        if p and q:
            break
    assert p * q == N
    return p, q


